import { Image, message } from 'antd'
import { notification } from 'antd/lib'
import * as qiniu from 'qiniu-js'
import { createRef, useState } from 'react'
import { v4 as uuid } from 'uuid'
export default function FileUploadPage() {
    const imagePreRef = createRef<HTMLImageElement>()
    const supportTypes = ['png', 'jpeg', 'gif', 'svg+xml']
    const [imageData, setImageData] = useState<string | undefined>()
    function isSuppot(v: string): boolean {
        console.log(v)
        return supportTypes.filter(item => item === v.substring(v.lastIndexOf("/") + 1)).length > 0
    }
    function genarFilename(type: string) {
        return uuid().replaceAll("-", '') + "." + type.substring(type.lastIndexOf("/") + 1)
    }

    function getUploadToken(filename: any): string | null {
        return null
    }
    const fileOnChange = (e: any) => {
        if (e.target.files.length === 0) {
            return
        }
        const file = e.target.files[0]
        if (!isSuppot(file.type)) {
            message.error("不支持的文件类型")
            return
        }

        const reader = new FileReader()
        reader.onload = function (data) {
            if (typeof (data.target?.result) === "string") {
                setImageData(data.target?.result)
            }
        }
        reader.readAsDataURL(file)



        const key = "erp/" + genarFilename(file.type)
        const token = getUploadToken(key)
        if (token) {
            const observable = qiniu.upload(file, key, token, {}, {})
            observable.subscribe({
                complete: function (result) {
                    console.log(result)
                    notification.success({ message: "文件上传成功" })
                },
                error: function (err) {
                    console.log(err)
                    notification.error({ message: "上传失败" })
                }
            })
        }
    }

    return (
        <div className="bg-white p-10">
            <input type="file" onChange={fileOnChange} />
            <br />
            <div style={{ margin: "20px" }}>
                <Image src={imageData} style={{ height: "280px" }} fallback="data:image/png;base64,"
                />
            </div>
        </div>
    )
}
